ï»¿import { Action, Reducer } from 'redux';
import { AppThunkAction } from './';
import { routerActions } from 'connected-react-router';
import App from '../App';
import { parsePath } from 'history';

const apiUrl = "http://localhost:42899/";

// -----------------
// STATE - This defines the type of data maintained in the Redux store.

export interface TablaVehiculosState {
    isLoading: boolean;
    items: number;
    page: number;
    vehiculos: Vehiculo[];
    selectedVehiculo: Vehiculo | null;
    modalIsOpen: boolean;
    
}


export interface Vehiculo {
    numPedido: number;
    bastidor: number;
    modelo: string;
    matricula: string;
    fechaEntrega: string;
}

// -----------------
// ACTIONS - These are serializable (hence replayable) descriptions of state transitions.
// They do not themselves have any side-effects; they just describe something that is going to happen.

interface RequestTablaVehiculosAction {
    page: number;
    items: number;
    type: 'REQUEST_VEHICULOS';
}

interface ReceiveTablaVehiculosAction {
    type: 'RECEIVE_VEHICULOS';
    vehiculos: Vehiculo[];
}

interface SelectVehiculoTablaVehiculosAction {
    type: 'SELECT_VEHICULOS';
    selectedVehiculo: Vehiculo | null;
}

interface AddVehiculoTablaVehiculosAction {
    type: 'ADD_VEHICULOS';
    newVehiculo: Vehiculo | null | undefined;
}


interface CloseModalTablaVehiculosAction {
    type: 'CLOSE_MODAL';
}

interface OpenEmptyModalTablaVehiculosAction {
    type: 'OPEN_EMPTY_MODAL';
}

interface ChangeItemsTablaVehiculosAction {
    type: 'CHANGE_ITEMS';
    numItems: number;
}
interface NextPageTablaVehiculosAction {
    type: 'NEXT_PAGE';
    newPage: number;
}

interface PreviousPageTablaVehiculosAction {
    type: 'PREVIOUS_PAGE';
    newPage: number;
}

// Declare a 'discriminated union' type. This guarantees that all references to 'type' properties contain one of the
// declared type strings (and not any other arbitrary string).
type KnownAction =
    RequestTablaVehiculosAction |
    ReceiveTablaVehiculosAction |
    SelectVehiculoTablaVehiculosAction |
    AddVehiculoTablaVehiculosAction |
    CloseModalTablaVehiculosAction |
    OpenEmptyModalTablaVehiculosAction |
    ChangeItemsTablaVehiculosAction |
    NextPageTablaVehiculosAction |
    PreviousPageTablaVehiculosAction



// ----------------
// ACTION CREATORS - These are functions exposed to UI components that will trigger a state transition.
// They don't directly mutate state, but they can have external side-effects (such as loading data).

export const actionCreators = {
    requestVehiculos: (page: number, items: number): AppThunkAction<KnownAction> => (dispatch, getState) => {
        // Only load data if it's something we don't already have (and are not already loading)
        const appState = getState();
        console.log("Trying to get Vehiculos. Page: " + page + ",Items: " + items);
        if (appState && appState.tablaVehiculos) {
            fetch(apiUrl + 'api/Vehiculo/getVehiculos?Page=' + page + '&Items=' + items, {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    'mode': 'cors'
                }
            }).then(response => response.json() as Promise<Vehiculo[]>)
                .then(data => {
                     return dispatch({ type: 'RECEIVE_VEHICULOS', vehiculos: data });
                });

            dispatch({ type: 'RECEIVE_VEHICULOS', vehiculos: []  });
        }
    },

    selectVehiculo: (v: Vehiculo): AppThunkAction<KnownAction> => (dispatch, getState) => {
        const appState = getState();
        if (appState && appState.tablaVehiculos) {
            dispatch({ type: 'SELECT_VEHICULOS', selectedVehiculo: v });
        }
    },

    addVehiculo: (v: Vehiculo): AppThunkAction<KnownAction> => (dispatch, getState) => {
        const appState = getState();
        if (appState && appState.tablaVehiculos) {
            fetch(apiUrl + 'api/Vehiculo/addVehiculo', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'mode': 'cors'
                },
                body: JSON.stringify(v)
            })
            .then(response => response.json() as Promise<Vehiculo[]>)
            .then(data => {
                return dispatch({ type: 'RECEIVE_VEHICULOS', vehiculos: data });
            });

            dispatch({ type: 'RECEIVE_VEHICULOS', vehiculos: [] });
        }
    },

    closeModal: (): AppThunkAction<KnownAction> => (dispatch, getState) => {
        const appState = getState();
        if (appState && appState.tablaVehiculos) {
            dispatch({ type: 'CLOSE_MODAL' });
        }
    },

    openEmptyModal: (): AppThunkAction<KnownAction> => (dispatch, getState) => {
        const appState = getState();
        if (appState && appState.tablaVehiculos) {
            dispatch({ type: 'OPEN_EMPTY_MODAL' });
        }
    },

    changeItems: (newNumItems: number): AppThunkAction<KnownAction> => (dispatch, getState) => {
        const appState = getState();
        if (appState && appState.tablaVehiculos) {
            dispatch({ type: 'CHANGE_ITEMS', numItems: newNumItems });
        }
    },


    nextPage: (): AppThunkAction<KnownAction> => (dispatch, getState) => {
        const appState = getState();
        if (appState && appState.tablaVehiculos) {
            dispatch({ type: 'NEXT_PAGE', newPage: appState.tablaVehiculos.page + 1 });
        }
    },

    previousPage: (): AppThunkAction<KnownAction> => (dispatch, getState) => {
        const appState = getState();
        if (appState && appState.tablaVehiculos) {
            var correctNewPage = appState.tablaVehiculos.page - 1;
            if (correctNewPage < 0) {
                correctNewPage = 0;
            }
            dispatch({ type: 'PREVIOUS_PAGE', newPage: correctNewPage });
        }
    }




};

// ----------------
// REDUCER - For a given state and action, returns the new state. To support time travel, this must not mutate the old state.

const unloadedState: TablaVehiculosState =
{
    vehiculos: [
        { numPedido: 1, bastidor: 21, matricula: "4139HJK", modelo: "Seat Ibiza", fechaEntrega: "27/05/2022" },
        { numPedido: 2, bastidor: 22, matricula: "4139HJL", modelo: "Seat Tarraco", fechaEntrega: "27/06/2022" },
        { numPedido: 3, bastidor: 23, matricula: "4139HJM", modelo: "Seat Marbella", fechaEntrega: "27/07/2022" },
        { numPedido: 4, bastidor: 22, matricula: "4139HJN", modelo: "Fiat Multipla", fechaEntrega: "27/06/2022" },
        { numPedido: 5, bastidor: 23, matricula: "4139HJO", modelo: "Nissan Patrol", fechaEntrega: "27/07/2022" },
        { numPedido: 6, bastidor: 22, matricula: "4139HJP", modelo: "Opel Corsa", fechaEntrega: "27/06/2022" },
        { numPedido: 7, bastidor: 23, matricula: "4139HJQ", modelo: "Opel Astra", fechaEntrega: "27/07/2022" },
        { numPedido: 8, bastidor: 22, matricula: "4139HJR", modelo: "Seat LeÃ³n", fechaEntrega: "27/06/2022" },
        { numPedido: 9, bastidor: 23, matricula: "4139HJS", modelo: "Dacia Sandero", fechaEntrega: "27/07/2022" },
        { numPedido: 10, bastidor: 22, matricula: "4139HJT", modelo: "Audi TT", fechaEntrega: "27/06/2022" },
        { numPedido: 11, bastidor: 23, matricula: "4139HJU", modelo: "Peugeot 206", fechaEntrega: "27/07/2022" },
        { numPedido: 12, bastidor: 22, matricula: "4139HJV", modelo: "Peugeot 207", fechaEntrega: "27/06/2022" },
        { numPedido: 13, bastidor: 23, matricula: "4139HJW", modelo: "Peugeot 208", fechaEntrega: "27/07/2022" },
        { numPedido: 14, bastidor: 22, matricula: "4139HJX", modelo: "Peugeot 307", fechaEntrega: "27/06/2022" },
        { numPedido: 15, bastidor: 23, matricula: "4139HJY", modelo: "Volkswagen Golf", fechaEntrega: "27/07/2022" }
    ], items: 0, page: 0, isLoading: false, selectedVehiculo: null, modalIsOpen: false
};

export const reducer: Reducer<TablaVehiculosState> = (state: TablaVehiculosState | undefined, incomingAction: Action): TablaVehiculosState => {
    if (state === undefined) {
        return unloadedState;
    }

    const action = incomingAction as KnownAction;
    switch (action.type) {
        case 'REQUEST_VEHICULOS':
            return {
                ...state,
                isLoading: true
            };
        case 'RECEIVE_VEHICULOS':
            // Only accept the incoming data if it matches the most recent request. This ensures we correctly
            // handle out-of-order responses.
            return {
                ...state,
                vehiculos: action.vehiculos,
                modalIsOpen: false,
                isLoading: false,
                page: 0,
                selectedVehiculo: null
            };
        case 'SELECT_VEHICULOS':
            return {
                ...state,
                selectedVehiculo: action.selectedVehiculo,
                modalIsOpen: true
            };
        case 'ADD_VEHICULOS':
            return {
                ...state,
                isLoading: true
            };
        case 'OPEN_EMPTY_MODAL':
            return {
                ...state,
                modalIsOpen: true
            };

        case 'CLOSE_MODAL':
            return {
                ...state,
                modalIsOpen: false,
                selectedVehiculo: null
            };
        case 'CHANGE_ITEMS':
            return {
                ...state,
                items: action.numItems
            };
        case 'NEXT_PAGE':
            return {
                ...state,
                page: action.newPage
            };

        case 'PREVIOUS_PAGE':
            return {
                ...state,
                page: action.newPage
            };
        default:
            return state;


    }

};
